mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select dish.*, category.name as categoryName from dish left join category on dish.category_id = category.id

    dish.category_id = #{categoryId}
    and dish.name like concat('%',#{name}, '%')
    and dish.status = #{status}
 order by update_time desc

select * from dish

    category_id = #{categoryId}
    and status = #{status}
